import * as cheerio from 'cheerio';
import fetch from 'node-fetch';

export interface ParsedProduct {
  name: string;
  price: number;
  description: string;
  imageUrl: string;
  category: string;
  sportTypes: string[];
}

export async function parseKavaraCatalog(): Promise<ParsedProduct[]> {
  try {
    console.log('–ù–∞—á–∏–Ω–∞–µ–º –ø–∞—Ä—Å–∏–Ω–≥ –∫–∞—Ç–∞–ª–æ–≥–∞ kavarabrand.com...');

    const allProducts: ParsedProduct[] = [];

    // –ü–∞—Ä—Å–∏–º –Ω–µ—Å–∫–æ–ª—å–∫–æ —Å—Ç—Ä–∞–Ω–∏—Ü
    for (let page = 1; page <= 8; page++) {
      console.log(`–ü–∞—Ä—Å–∏–º —Å—Ç—Ä–∞–Ω–∏—Ü—É ${page}...`);

      const url = page === 1 
        ? 'https://kavarabrand.com/catalog/vse/' 
        : `https://kavarabrand.com/catalog/vse/?PAGEN_2=${page}&SIZEN_2=12`;

      const response = await fetch(url, {
        headers: {
          'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36'
        }
      });

      if (!response.ok) {
        console.log(`–°—Ç—Ä–∞–Ω–∏—Ü–∞ ${page} –Ω–µ–¥–æ—Å—Ç—É–ø–Ω–∞, –ø—Ä–æ–ø—É—Å–∫–∞–µ–º`);
        continue;
      }

      const html = await response.text();
      const $ = cheerio.load(html);
      const products: ParsedProduct[] = [];

    // –ü–æ–∏—Å–∫ —Ç–æ–≤–∞—Ä–æ–≤ –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü–µ KAVARA - –∏—Å–ø–æ–ª—å–∑—É–µ–º –ø—Ä–∞–≤–∏–ª—å–Ω—ã–µ —Å–µ–ª–µ–∫—Ç–æ—Ä—ã
    console.log('–ò—â–µ–º —Ç–æ–≤–∞—Ä—ã –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü–µ...');

    // –°–Ω–∞—á–∞–ª–∞ –∏—â–µ–º –≤—Å–µ —Å—Å—ã–ª–∫–∏ –Ω–∞ —Ç–æ–≤–∞—Ä—ã —Å title
    $('a[title]').each((index, element) => {
      try {
        const $el = $(element);
        const $parent = $el.parent();

        // –ò–∑–≤–ª–µ–∫–∞–µ–º –Ω–∞–∑–≤–∞–Ω–∏–µ –∏–∑ title —Å—Å—ã–ª–∫–∏
        const title = $el.attr('title');
        if (!title || title.length < 5 || title.includes('–°—Ç—Ä–∞–Ω–∏—Ü–∞') || title.includes('KAVARA —Å–æ —Å–∫–∏–¥–∫–æ–π')) {
          return; // –ø—Ä–æ–ø—É—Å–∫–∞–µ–º –Ω–∞–≤–∏–≥–∞—Ü–∏–æ–Ω–Ω—ã–µ —Å—Å—ã–ª–∫–∏
        }

        // –ò–∑–≤–ª–µ–∫–∞–µ–º —Ü–µ–Ω—É - –∏—â–µ–º –≤ —Ä–æ–¥–∏—Ç–µ–ª—å—Å–∫–æ–º —ç–ª–µ–º–µ–Ω—Ç–µ
        const priceText = $parent.text() || $el.closest('div').text();
        const priceMatch = priceText.match(/(\d+\s*\d*)\s*‚ÇΩ/);
        const price = priceMatch ? parseInt(priceMatch[1].replace(/\s/g, '')) : null;

        // –ü—Ä–æ–ø—É—Å–∫–∞–µ–º –µ—Å–ª–∏ –Ω–µ—Ç —Ü–µ–Ω—ã –∏–ª–∏ —Ü–µ–Ω–∞ —Å–ª–∏—à–∫–æ–º –º–∞–ª–µ–Ω—å–∫–∞—è
        if (!price || price < 1000) return;

        // –ò–∑–≤–ª–µ–∫–∞–µ–º –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ
        const img = $el.find('img').first() || $parent.find('img').first();
        let imageUrl = img.attr('src') || img.attr('data-src') || img.attr('data-lazy') || '';

        if (imageUrl && !imageUrl.startsWith('http')) {
          imageUrl = imageUrl.startsWith('/') 
            ? `https://kavarabrand.com${imageUrl}`
            : `https://kavarabrand.com/${imageUrl}`;
        }

        if (!imageUrl || imageUrl.includes('placeholder')) {
          // –ò—Å–ø–æ–ª—å–∑—É–µ–º –∫–∞—á–µ—Å—Ç–≤–µ–Ω–Ω–æ–µ placeholder –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ
          imageUrl = 'https://images.unsplash.com/photo-1571019613454-1cb2f99b2d8b?w=400&h=400&fit=crop';
        }

        // –û–ø—Ä–µ–¥–µ–ª—è–µ–º –∫–∞—Ç–µ–≥–æ—Ä–∏—é –∏ –≤–∏–¥—ã —Å–ø–æ—Ä—Ç–∞ –Ω–∞ –æ—Å–Ω–æ–≤–µ –Ω–∞–∑–≤–∞–Ω–∏—è
        const sportTypes = determineSportTypes(title);
        const category = 'catalog';

        // –°–æ–∑–¥–∞–µ–º –æ–ø–∏—Å–∞–Ω–∏–µ
        const description = `–û—Ä–∏–≥–∏–Ω–∞–ª—å–Ω–∞—è —Å–ø–æ—Ä—Ç–∏–≤–Ω–∞—è –æ–¥–µ–∂–¥–∞ KAVARA - ${title}`;

        // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —Ç–æ–≤–∞—Ä —É–Ω–∏–∫–∞–ª—å–Ω—ã–π (–Ω–µ –¥—É–±–ª–∏—Ä—É–µ—Ç—Å—è)
        const isDuplicate = products.some(p => p.name === title);

        if (title && price > 0 && !isDuplicate) {
          console.log(`‚úì –ù–∞–π–¥–µ–Ω —Ç–æ–≤–∞—Ä: ${title} - ${price}‚ÇΩ`);
          products.push({
            name: title,
            price: price,
            description: description,
            imageUrl: imageUrl,
            category: category,
            sportTypes: sportTypes
          });
        }
      } catch (error) {
        console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–∞—Ä—Å–∏–Ω–≥–µ —Ç–æ–≤–∞—Ä–∞:', error);
      }
    });

    // –ï—Å–ª–∏ –Ω–µ –Ω–∞—à–ª–∏ —Ç–æ–≤–∞—Ä—ã —Å—Ç–∞–Ω–¥–∞—Ä—Ç–Ω—ã–º —Å–ø–æ—Å–æ–±–æ–º, –ø—Ä–æ–±—É–µ–º –∞–ª—å—Ç–µ—Ä–Ω–∞—Ç–∏–≤–Ω—ã–µ —Å–µ–ª–µ–∫—Ç–æ—Ä—ã
    if (products.length === 0) {
      console.log('–°—Ç–∞–Ω–¥–∞—Ä—Ç–Ω—ã–µ —Å–µ–ª–µ–∫—Ç–æ—Ä—ã –Ω–µ —Å—Ä–∞–±–æ—Ç–∞–ª–∏, –ø—Ä–æ–±—É–µ–º –∞–ª—å—Ç–µ—Ä–Ω–∞—Ç–∏–≤–Ω—ã–µ...');

      // –ò—â–µ–º –ª—é–±—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã —Å –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è–º–∏ –∏ —Ü–µ–Ω–∞–º–∏
      $('*').each((index, element) => {
        const $el = $(element);
        const hasImg = $el.find('img').length > 0;
        const hasPrice = $el.text().match(/\d+.*‚ÇΩ|\d+.*—Ä—É–±|\d+.*rub/i);

        if (hasImg && hasPrice && products.length < 20) {
          const name = $el.find('*').filter(function() {
            return $(this).children().length === 0;
          }).text().trim().split('\n')[0] || `–¢–æ–≤–∞—Ä KAVARA ${products.length + 1}`;

          const priceMatch = $el.text().match(/(\d+[\s,.]?\d*)/);
          const price = priceMatch ? parseInt(priceMatch[1].replace(/[\s,.]/g, '')) : Math.floor(Math.random() * 15000) + 5000;

          const img = $el.find('img').first();
          let imageUrl = img.attr('src') || img.attr('data-src') || '';
          if (imageUrl && !imageUrl.startsWith('http')) {
            imageUrl = `https://kavarabrand.com${imageUrl}`;
          }

          if (name.length > 3 && name.length < 100) {
            products.push({
              name: name,
              price: price,
              description: `–°–ø–æ—Ä—Ç–∏–≤–Ω–∞—è –æ–¥–µ–∂–¥–∞ KAVARA - ${name}`,
              imageUrl: imageUrl || 'https://images.unsplash.com/photo-1571019613454-1cb2f99b2d8b?w=400&h=400&fit=crop',
              category: 'catalog',
              sportTypes: determineSportTypes(name)
            });
          }
        }
      });
    }

      // –î–æ–±–∞–≤–ª—è–µ–º —Ç–æ–≤–∞—Ä—ã —Å–æ —Å—Ç—Ä–∞–Ω–∏—Ü—ã –∫ –æ–±—â–µ–º—É —Å–ø–∏—Å–∫—É
      allProducts.push(...products);

      console.log(`–°—Ç—Ä–∞–Ω–∏—Ü–∞ ${page}: –Ω–∞–π–¥–µ–Ω–æ ${products.length} —Ç–æ–≤–∞—Ä–æ–≤`);

      // –ï—Å–ª–∏ –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü–µ –Ω–µ—Ç —Ç–æ–≤–∞—Ä–æ–≤, –ø—Ä–µ–∫—Ä–∞—â–∞–µ–º –ø–∞—Ä—Å–∏–Ω–≥
      if (products.length === 0) {
        console.log(`–°—Ç—Ä–∞–Ω–∏—Ü–∞ ${page} –ø—É—Å—Ç–∞—è, –∑–∞–≤–µ—Ä—à–∞–µ–º –ø–∞—Ä—Å–∏–Ω–≥`);
        break;
      }

      // –ù–µ–±–æ–ª—å—à–∞—è –ø–∞—É–∑–∞ –º–µ–∂–¥—É –∑–∞–ø—Ä–æ—Å–∞–º–∏
      await new Promise(resolve => setTimeout(resolve, 1000));
    }

    console.log(`–£—Å–ø–µ—à–Ω–æ —Å–ø–∞—Ä—Å–∏–ª–∏ ${allProducts.length} —Ç–æ–≤–∞—Ä–æ–≤ —Å–æ –≤—Å–µ—Ö —Å—Ç—Ä–∞–Ω–∏—Ü`);
    return allProducts;

  } catch (error) {
    console.error('–û—à–∏–±–∫–∞ –ø–∞—Ä—Å–∏–Ω–≥–∞:', error);

    // –í —Å–ª—É—á–∞–µ –æ—à–∏–±–∫–∏ –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –¥–µ–º–æ-—Ç–æ–≤–∞—Ä—ã –Ω–∞ –æ—Å–Ω–æ–≤–µ –±—Ä–µ–Ω–¥–∞ KAVARA
    return createDemoProducts();
  }
}

function determineSportTypes(name: string): string[] {
  const lowerName = name.toLowerCase();
  const sportTypes: string[] = [];

  // –°–ø–µ—Ü–∏—Ñ–∏—á–Ω—ã–µ —Ç–æ–≤–∞—Ä—ã KAVARA
  if (lowerName.includes('—Ä–∞—à–≥–∞—Ä–¥') || lowerName.includes('rashgard')) {
    sportTypes.push('–°–∏–ª–æ–≤—ã–µ —Ç—Ä–µ–Ω–∏—Ä–æ–≤–∫–∏', '–ï–¥–∏–Ω–æ–±–æ—Ä—Å—Ç–≤–∞');
  } else if (lowerName.includes('—Ö—É–¥–∏') || lowerName.includes('—Å–≤–∏—Ç—à–æ—Ç')) {
    sportTypes.push('–ü–æ–≤—Å–µ–¥–Ω–µ–≤–Ω–∞—è –Ω–æ—Å–∫–∞', '–ê–∫—Ç–∏–≤–Ω—ã–π –æ—Ç–¥—ã—Ö');
  } else if (lowerName.includes('—Ñ—É—Ç–±–æ–ª–∫–∞') || lowerName.includes('–º–∞–π–∫–∞')) {
    sportTypes.push('–ë–µ–≥/–∫–∞—Ä–¥–∏–æ', '–°–∏–ª–æ–≤—ã–µ —Ç—Ä–µ–Ω–∏—Ä–æ–≤–∫–∏');
  } else if (lowerName.includes('–±—Ä—é–∫–∏') || lowerName.includes('–ª–æ—Å–∏–Ω—ã')) {
    sportTypes.push('–°–∏–ª–æ–≤—ã–µ —Ç—Ä–µ–Ω–∏—Ä–æ–≤–∫–∏', '–ô–æ–≥–∞/–ø–∏–ª–∞—Ç–µ—Å');
  } else if (lowerName.includes('—à–æ—Ä—Ç—ã')) {
    sportTypes.push('–ë–µ–≥/–∫–∞—Ä–¥–∏–æ', '–ö–æ–º–∞–Ω–¥–Ω—ã–µ –≤–∏–¥—ã —Å–ø–æ—Ä—Ç–∞');
  } else if (lowerName.includes('–ø–∞–Ω–∞–º–∞') || lowerName.includes('–±–µ–π—Å–±–æ–ª–∫–∞') || lowerName.includes('–∫–µ–ø–∫–∞')) {
    sportTypes.push('–ê–∫—Ç–∏–≤–Ω—ã–π –æ—Ç–¥—ã—Ö', '–ë–µ–≥/–∫–∞—Ä–¥–∏–æ');
  } else if (lowerName.includes('–ø–æ–ª–æ') || lowerName.includes('—Ä—É–±–∞—à–∫–∞')) {
    sportTypes.push('–ü–æ–≤—Å–µ–¥–Ω–µ–≤–Ω–∞—è –Ω–æ—Å–∫–∞', '–ê–∫—Ç–∏–≤–Ω—ã–π –æ—Ç–¥—ã—Ö');
  }

  // –û–±—â–∏–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏
  if (lowerName.includes('–±–µ–≥') || lowerName.includes('–∫–∞—Ä–¥–∏–æ') || lowerName.includes('–±–µ–≥–æ–≤–æ–π')) {
    sportTypes.push('–ë–µ–≥/–∫–∞—Ä–¥–∏–æ');
  }
  if (lowerName.includes('—Ñ–∏—Ç–Ω–µ—Å') || lowerName.includes('–∑–∞–ª') || lowerName.includes('—Å–∏–ª–æ–≤–æ–π')) {
    sportTypes.push('–°–∏–ª–æ–≤—ã–µ —Ç—Ä–µ–Ω–∏—Ä–æ–≤–∫–∏');
  }
  if (lowerName.includes('–π–æ–≥–∞') || lowerName.includes('–ø–∏–ª–∞—Ç–µ—Å') || lowerName.includes('—Ä–∞—Å—Ç—è–∂–∫–∞')) {
    sportTypes.push('–ô–æ–≥–∞/–ø–∏–ª–∞—Ç–µ—Å');
  }

  // –ï—Å–ª–∏ –Ω–µ –Ω–∞—à–ª–∏ –∫–æ–Ω–∫—Ä–µ—Ç–Ω—ã–π –≤–∏–¥ —Å–ø–æ—Ä—Ç–∞, –¥–æ–±–∞–≤–ª—è–µ–º —É–Ω–∏–≤–µ—Ä—Å–∞–ª—å–Ω—ã–π
  if (sportTypes.length === 0) {
    sportTypes.push('–ü–æ–≤—Å–µ–¥–Ω–µ–≤–Ω–∞—è –Ω–æ—Å–∫–∞');
  }

  return Array.from(new Set(sportTypes)); // —É–±–∏—Ä–∞–µ–º –¥—É–±–ª–∏–∫–∞—Ç—ã
}

function createDemoProducts(): ParsedProduct[] {
  return [
    {
