router.get("/api/catalog", async (req, res) => {
  try {
    const { sportType, minPrice, maxPrice } = req.query;

    // –ü–æ–ª—É—á–∞–µ–º —Ç–æ–ª—å–∫–æ –æ—Ç–¥–µ–ª—å–Ω—ã–µ —Ç–æ–≤–∞—Ä—ã (Products), –ù–ï –±–æ–∫—Å—ã
