  @Column({ type: "varchar" })
  paymentMethod!: string;

  @Column({ type: "integer" })
  totalPrice!: number;

  @Column({ type: "varchar", default: "pending" })
  status!: string;

  @Column({ type: "varchar", nullable: true })
  paymentId?: string;

  @Column({ type: "uuid", nullable: true })
