import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { ArrowLeft, CreditCard, ExternalLink } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { Order, Box } from "@shared/schema";

interface PaymentIntent {
  id: string;
  amount: number;
  status: 'pending' | 'succeeded' | 'failed';
  paymentUrl?: string;
  description: string;
  orderId: string;
}

export default function Checkout() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [order, setOrder] = useState<Order | null>(null);
  const [box, setBox] = useState<Box | null>(null);
  const [paymentIntent, setPaymentIntent] = useState<PaymentIntent | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isCreatingPayment, setIsCreatingPayment] = useState(false);

  useEffect(() => {
    const orderData = sessionStorage.getItem("currentOrder");
    const boxData = sessionStorage.getItem("selectedBox");

    if (orderData && boxData) {
      setOrder(JSON.parse(orderData));
      setBox(JSON.parse(boxData));
    } else {
      setLocation("/");
    }
    setIsLoading(false);
  }, [setLocation]);

  const createPayment = async () => {
    if (!order || !box) return;

    setIsCreatingPayment(true);
    try {
      const response = await apiRequest("POST", "/api/create-payment-intent", {
        amount: order.totalPrice,
        description: `–û–ø–ª–∞—Ç–∞ –∑–∞–∫–∞–∑–∞ ${order.orderNumber} - ${box.name}`,
        orderId: order.orderNumber,
        returnUrl: `${window.location.origin}/payment/success`
      });

      const intent = await response.json();
      setPaymentIntent(intent);

      // Store payment ID in the order
      if (intent.id && order.id) {
        try {
          await apiRequest("PUT", `/api/orders/${order.id}/payment-id`, {
            paymentId: intent.id
          });
          console.log(`Payment ID ${intent.id} saved for order ${order.orderNumber}`);
        } catch (error) {
          console.error("Failed to update order with payment ID:", error);
        }
      }

      if (intent.paymentUrl) {
        window.open(intent.paymentUrl, '_blank');
      }
    } catch (error) {
      console.error("Payment creation error:", error);
      toast({
        title: "–û—à–∏–±–∫–∞",
        description: "–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ–∑–¥–∞—Ç—å –ø–ª–∞—Ç–µ–∂. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â—ë —Ä–∞–∑.",
        variant: "destructive",
      });
    } finally {
      setIsCreatingPayment(false);
    }
  };

  const checkPaymentStatus = async () => {
    if (!paymentIntent || !order) return;

    try {
      // Check order status directly by order number
      const response = await apiRequest("GET", `/api/orders/number/${order.orderNumber}`);
      const orderData = await response.json();

      if (orderData.status === 'paid') {
        toast({
          title: "–û–ø–ª–∞—Ç–∞ –ø—Ä–æ—à–ª–∞ —É—Å–ø–µ—à–Ω–æ!",
          description: "–í–∞—à –∑–∞–∫–∞–∑ –ø—Ä–∏–Ω—è—Ç –≤ –æ–±—Ä–∞–±–æ—Ç–∫—É.",
        });
        // Store order data in session for payment success page
        sessionStorage.setItem("currentOrder", JSON.stringify(orderData));
        sessionStorage.setItem("selectedBox", JSON.stringify(box));
        setLocation("/payment/success");
      } else {
        toast({
          title: "–ü–ª–∞—Ç–µ–∂ –µ—â–µ –Ω–µ –ø—Ä–æ—à–µ–ª",
          description: "–ü—Ä–æ–≤–µ—Ä—å—Ç–µ, —á—Ç–æ –æ–ø–ª–∞—Ç–∞ –∑–∞–≤–µ—Ä—à–µ–Ω–∞ –≤ –ÆMoney –∏ –ø–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.",
          variant: "destructive",
        });
      }
    } catch (error) {
      console.error("Payment status check error:", error);
      toast({
        title: "–û—à–∏–±–∫–∞ –ø—Ä–æ–≤–µ—Ä–∫–∏ —Å—Ç–∞—Ç—É—Å–∞",
        description: "–ù–µ —É–¥–∞–ª–æ—Å—å –ø—Ä–æ–≤–µ—Ä–∏—Ç—å —Å—Ç–∞—Ç—É—Å –ø–ª–∞—Ç–µ–∂–∞. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.",
        variant: "destructive",
      });
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  if (!order || !box) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <p className="text-gray-600">–ó–∞–∫–∞–∑ –Ω–µ –Ω–∞–π–¥–µ–Ω</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="p-4 bg-black text-white">
        <div className="flex items-center space-x-3">
          <button 
            className="p-2" 
            onClick={() => setLocation("/order")}
          >
            <ArrowLeft className="w-6 h-6" />
          </button>
          <div>
            <h2 className="font-semibold">–û–ø–ª–∞—Ç–∞ –∑–∞–∫–∞–∑–∞</h2>
            <p className="text-sm text-white/80">#{order.orderNumber}</p>
          </div>
        </div>
      </div>

      <div className="p-4 space-y-4">
        {/* Order Summary */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center justify-between">
              <span>–î–µ—Ç–∞–ª–∏ –∑–∞–∫–∞–∑–∞</span>
              <Badge variant="outline">{order.status}</Badge>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex items-center space-x-4">
              <img 
                src={box.imageUrl} 
                alt={box.name}
                className="w-16 h-16 rounded-lg object-cover"
              />
              <div className="flex-1">
                <h3 className="font-semibold">{box.name}</h3>
                <p className="text-sm text-gray-600">{box.description}</p>
              </div>
              <div className="text-right">
                <p className="font-bold">{order.totalPrice}‚ÇΩ</p>
              </div>
            </div>

            <div className="border-t pt-4 space-y-2">
              <div className="flex justify-between">
                <span className="text-gray-600">–î–æ—Å—Ç–∞–≤–∫–∞:</span>
                <span>{order.deliveryMethod === 'courier' ? '–ö—É—Ä—å–µ—Ä' : order.deliveryMethod === 'pickup' ? '–°–∞–º–æ–≤—ã–≤–æ–∑' : '–ü–æ—á—Ç–∞'}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">–ü–æ–ª—É—á–∞—Ç–µ–ª—å:</span>
                <span>{order.customerName}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">–¢–µ–ª–µ—Ñ–æ–Ω:</span>
                <span>{order.customerPhone}</span>
              </div>
              {order.customerEmail && (
                <div className="flex justify-between">
                  <span className="text-gray-600">Email:</span>
                  <span>{order.customerEmail}</span>
                </div>
              )}
            </div>

            <div className="border-t pt-4">
              <div className="flex justify-between items-center text-lg font-bold">
                <span>–ö –æ–ø–ª–∞—Ç–µ:</span>
                <span>{order.totalPrice}‚ÇΩ</span>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Payment Methods */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center space-x-2">
              <CreditCard className="w-5 h-5" />
              <span>–°–ø–æ—Å–æ–± –æ–ø–ª–∞—Ç—ã</span>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="p-4 border rounded-lg bg-blue-50">
              <div className="flex items-center space-x-3">
                <div className="w-12 h-12 bg-blue-600 rounded-lg flex items-center justify-center">
                  <span className="text-white font-bold text-sm">–Æ</span>
                </div>
                <div className="flex-1">
                  <h4 className="font-semibold">–ÆMoney</h4>
                  <p className="text-sm text-gray-600">
                    –ë–µ–∑–æ–ø–∞—Å–Ω–∞—è –æ–ø–ª–∞—Ç–∞ —á–µ—Ä–µ–∑ –ÆMoney
                  </p>
                </div>
              </div>
            </div>

            <div className="space-y-3">
              {!paymentIntent ? (
                <Button 
                  onClick={createPayment}
                  disabled={isCreatingPayment}
                  className="w-full bg-blue-600 hover:bg-blue-700 text-white py-3"
                  size="lg"
                >
                  {isCreatingPayment ? (
                    <>
                      <div className="animate-spin w-4 h-4 border-2 border-white border-t-transparent rounded-full mr-2" />
                      –°–æ–∑–¥–∞—ë–º –ø–ª–∞—Ç–µ–∂...
                    </>
                  ) : (
                    <>
                      <CreditCard className="w-5 h-5 mr-2" />
                      –û–ø–ª–∞—Ç–∏—Ç—å {order.totalPrice}‚ÇΩ
                    </>
                  )}
                </Button>
              ) : (
                <div className="space-y-3">
                  <div className="p-4 bg-green-50 border border-green-200 rounded-lg">
                    <h4 className="font-semibold text-green-800 mb-2">
                      –ü–ª–∞—Ç–µ–∂ —Å–æ–∑–¥–∞–Ω
