        </div>
      </div>
    );
  }

  const availableBoxes = readyBoxes?.filter(box => box.isAvailable) || [];
  const comingSoonBoxes = readyBoxes?.filter(box => !box.isAvailable) || [];

  return (
    <div className="min-h-screen bg-white">
      <div className="p-6 border-b border-gray-200">
        <div className="flex items-center space-x-4">
          <button 
            className="p-2 -ml-2" 
            onClick={() => setLocation("/")}
          >
            <ArrowLeft className="w-6 h-6 text-black" />
          </button>
          <div>
            <h2 className="text-2xl font-bold text-black tracking-wide">–ì–û–¢–û–í–´–ï –ö–û–õ–õ–ï–ö–¶–ò–ò</h2>
            <p className="text-gray-600 font-medium">–í–´–ë–ï–†–ò–¢–ï –ì–û–¢–û–í–´–ô –ö–û–ú–ü–õ–ï–ö–¢</p>
          </div>
        </div>
      </div>

      <div className="p-6">
