        <div className="mb-8">
          <h3 className="text-xl font-bold text-black mb-6 tracking-wide">–î–û–°–¢–£–ü–ù–û –°–ï–ô–ß–ê–°</h3>
          <div className="space-y-6">
            {availableBoxes.map((box) => (
              <BoxCard
                key={box.id}
                box={box}
                onSelect={handleSelectBox}
                variant="default"
                userId={user?.id?.toString()}
              />
            ))}
          </div>
        </div>

        {/* Coming Soon Section */}
        {comingSoonBoxes.length > 0 && (
          <div>
            <h3 className="text-xl font-bold text-black mb-6 tracking-wide">–°–ö–û–†–û –í –ü–†–û–î–ê–ñ–ï</h3>
            <div className="space-y-6">
              {comingSoonBoxes.map((box) => (
                <BoxCard
                  key={box.id}
                  box={box}
                  onNotify={handleNotifyMe}
                  variant="coming-soon"
                  userId={user?.id?.toString()}
                />
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

