import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useToast } from "@/hooks/use-toast";
import { ArrowLeft, Plus, Gift, BarChart3, Calendar, Percent } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

interface PromoCode {
  id: string;
  code: string;
  discountPercent: number;
  maxUses: number;
  usedCount: number;
  isActive: boolean;
  partnerName: string;
  partnerContact: string;
  createdAt: string;
  expiresAt?: string;
  orders?: any[];
}

interface PromoCodeFormData {
  code: string;
  discountPercent: number;
  maxUses: number;
  partnerName: string;
  partnerContact: string;
  expiresAt?: string;
}

export default function PromoCodes({ onBack }: { onBack: () => void }) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showForm, setShowForm] = useState(false);
  const [selectedPromoCode, setSelectedPromoCode] = useState<PromoCode | null>(null);
  const [formData, setFormData] = useState<PromoCodeFormData>({
    code: '',
    discountPercent: 10,
    maxUses: 100,
    partnerName: '',
    partnerContact: '',
    expiresAt: ''
  });

  const { data: promoCodes, isLoading } = useQuery<PromoCode[]>({
    queryKey: ["/api/admin/promo-codes"],
    retry: false,
  });

  const createPromoCodeMutation = useMutation({
    mutationFn: async (data: PromoCodeFormData) => {
      const response = await apiRequest("POST", "/api/admin/promo-codes", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/promo-codes"] });
      setShowForm(false);
      setFormData({
        code: '',
        discountPercent: 10,
        maxUses: 100,
        partnerName: '',
        partnerContact: '',
        expiresAt: ''
      });
      toast({
        title: "–ü—Ä–æ–º–æ–∫–æ–¥ —Å–æ–∑–¥–∞–Ω",
        description: "–ù–æ–≤—ã–π –ø—Ä–æ–º–æ–∫–æ–¥ —É—Å–ø–µ—à–Ω–æ –¥–æ–±–∞–≤–ª–µ–Ω –≤ —Å–∏—Å—Ç–µ–º—É"
      });
    },
    onError: (error: any) => {
      toast({
        title: "–û—à–∏–±–∫–∞",
        description: error.message || "–ù–µ —É–¥–∞–ª–æ—Å—å —Å–æ–∑–¥–∞—Ç—å –ø—Ä–æ–º–æ–∫–æ–¥",
        variant: "destructive"
      });
    }
  });

  const togglePromoCodeMutation = useMutation({
    mutationFn: async ({ id, isActive }: { id: string; isActive: boolean }) => {
      const response = await apiRequest("PUT", `/api/admin/promo-codes/${id}/toggle`, { isActive });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/promo-codes"] });
      toast({
        title: "–°—Ç–∞—Ç—É—Å –æ–±–Ω–æ–≤–ª–µ–Ω",
        description: "–°—Ç–∞—Ç—É—Å –ø—Ä–æ–º–æ–∫–æ–¥–∞ —É—Å–ø–µ—à–Ω–æ –∏–∑–º–µ–Ω–µ–Ω"
      });
    }
  });

  const generateRandomCode = () => {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
    let result = '';
    for (let i = 0; i < 8; i++) {
      result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    setFormData({ ...formData, code: result });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.code || !formData.partnerName || !formData.partnerContact) {
      toast({
        title: "–û—à–∏–±–∫–∞",
        description: "–ó–∞–ø–æ–ª–Ω–∏—Ç–µ –≤—Å–µ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–µ –ø–æ–ª—è",
        variant: "destructive"
      });
      return;
    }
    createPromoCodeMutation.mutate(formData);
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-black border-t-transparent rounded-full" />
      </div>
    );
  }

  // –ü–æ–∫–∞–∑–∞—Ç—å –¥–µ—Ç–∞–ª–∏ –ø—Ä–æ–º–æ–∫–æ–¥–∞ —Å –∑–∞–∫–∞–∑–∞–º–∏
  if (selectedPromoCode) {
    return (
      <div className="min-h-screen bg-gray-50 p-6">
        <div className="max-w-6xl mx-auto">
          <div className="flex items-center gap-4 mb-6">
            <Button 
              variant="outline" 
              onClick={() => setSelectedPromoCode(null)}
              className="flex items-center gap-2"
            >
              <ArrowLeft className="h-4 w-4" />
              –ù–∞–∑–∞–¥ –∫ –ø—Ä–æ–º–æ–∫–æ–¥–∞–º
            </Button>
            <h1 className="text-2xl font-bold">–ü—Ä–æ–º–æ–∫–æ–¥: {selectedPromoCode.code}</h1>
          </div>

          <div className="grid md:grid-cols-3 gap-6 mb-6">
            <Card>
              <CardContent className="p-6 text-center">
                <Percent className="h-8 w-8 mx-auto mb-2 text-green-500" />
                <div className="text-2xl font-bold">{selectedPromoCode.discountPercent}%</div>
                <p className="text-sm text-muted-foreground">–†–∞–∑–º–µ—Ä —Å–∫–∏–¥–∫–∏</p>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6 text-center">
                <BarChart3 className="h-8 w-8 mx-auto mb-2 text-blue-500" />
                <div className="text-2xl font-bold">{selectedPromoCode.usedCount} / {selectedPromoCode.maxUses}</div>
                <p className="text-sm text-muted-foreground">–ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–π</p>
              </CardContent>
            </Card>

            <Card>
              <CardContent className="p-6 text-center">
                <Calendar className="h-8 w-8 mx-auto mb-2 text-purple-500" />
                <div className="text-2xl font-bold">
                  {selectedPromoCode.expiresAt 
                    ? new Date(selectedPromoCode.expiresAt).toLocaleDateString('ru-RU')
                    : '–ë–µ—Å—Å—Ä–æ—á–Ω–æ'
                  }
                </div>
                <p className="text-sm text-muted-foreground">–°—Ä–æ–∫ –¥–µ–π—Å—Ç–≤–∏—è</p>
              </CardContent>
            </Card>
          </div>

          <Card className="mb-6">
            <CardHeader>
              <CardTitle>–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–∞—Ä—Ç–Ω–µ—Ä–µ</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <Label className="text-sm font-medium">–ù–∞–∑–≤–∞–Ω–∏–µ –ø–∞—Ä—Ç–Ω–µ—Ä–∞</Label>
                  <p className="text-lg">{selectedPromoCode.partnerName}</p>
                </div>
                <div>
                  <Label className="text-sm font-medium">–ö–æ–Ω—Ç–∞–∫—Ç—ã</Label>
                  <p className="text-lg">{selectedPromoCode.partnerContact}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>–ó–∞–∫–∞–∑—ã —Å —ç—Ç–∏–º –ø—Ä–æ–º–æ–∫–æ–¥–æ–º</CardTitle>
              <CardDescription>–ò—Å—Ç–æ—Ä–∏—è –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è –ø—Ä–æ–º–æ–∫–æ–¥–∞</CardDescription>
            </CardHeader>
            <CardContent>
              {selectedPromoCode.orders && selectedPromoCode.orders.length > 0 ? (
                <div className="space-y-4">
                  {selectedPromoCode.orders.map((order: any) => (
                    <div key={order.id} className="flex justify-between items-center p-4 border rounded-lg">
                      <div>
                        <p className="font-medium">–ó–∞–∫–∞–∑ #{order.orderNumber}</p>
                        <p className="text-sm text-gray-600">{order.customerName}</p>
                        <p className="text-xs text-gray-500">{new Date(order.createdAt).toLocaleString('ru-RU')}</p>
                      </div>
                      <div className="text-right">
                        <p className="font-bold">{order.totalPrice}‚ÇΩ</p>
                        <p className="text-sm text-green-600">
                          –°–∫–∏–¥–∫–∞: {order.discountAmount || Math.floor(order.totalPrice * selectedPromoCode.discountPercent / 100)}‚ÇΩ
                        </p>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="text-center py-8">
