import { Heart } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useIsFavorite, useFavoriteMutations } from "@/hooks/use-favorites";
import { useToast } from "@/hooks/use-toast";
import { cn } from "@/lib/utils";

interface FavoriteButtonProps {
  boxId: string;
  userId?: string;
  size?: "sm" | "md" | "lg";
  variant?: "ghost" | "outline" | "default";
  className?: string;
}

export function FavoriteButton({
  boxId,
  userId,
  size = "md",
  variant = "ghost",
  className,
}: FavoriteButtonProps) {
  const { toast } = useToast();
  const { data: favoriteStatus } = useIsFavorite(userId, boxId);
  const { toggleFavorite, isLoading } = useFavoriteMutations(userId);

  const isFavorited = favoriteStatus?.isFavorite || false;

  const handleToggleFavorite = async () => {
    if (!userId) {
      toast({
        title: "–í–æ–π–¥–∏—Ç–µ –≤ –∞–∫–∫–∞—É–Ω—Ç",
        description: "–î–ª—è –¥–æ–±–∞–≤–ª–µ–Ω–∏—è –≤ –∏–∑–±—Ä–∞–Ω–Ω–æ–µ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ –≤–æ–π—Ç–∏ –≤ –∞–∫–∫–∞—É–Ω—Ç",
        variant: "destructive",
      });
      return;
    }

    try {
      await toggleFavorite(boxId, isFavorited);

      toast({
        title: isFavorited ? "–£–¥–∞–ª–µ–Ω–æ –∏–∑ –∏–∑–±—Ä–∞–Ω–Ω–æ–≥–æ" : "–î–æ–±–∞–≤–ª–µ–Ω–æ –≤ –∏–∑–±—Ä–∞–Ω–Ω–æ–µ",
        description: isFavorited 
          ? "–¢–æ–≤–∞—Ä —É–¥–∞–ª–µ–Ω –∏–∑ –≤–∞—à–µ–≥–æ —Å–ø–∏—Å–∫–∞ –∏–∑–±—Ä–∞–Ω–Ω–æ–≥–æ" 
          : "–¢–æ–≤–∞—Ä –¥–æ–±–∞–≤–ª–µ–Ω –≤ –≤–∞—à —Å–ø–∏—Å–æ–∫ –∏–∑–±—Ä–∞–Ω–Ω–æ–≥–æ",
      });
    } catch (error) {
      toast({
        title: "–û—à–∏–±–∫–∞",
        description: "–ù–µ —É–¥–∞–ª–æ—Å—å –æ–±–Ω–æ–≤–∏—Ç—å –∏–∑–±—Ä–∞–Ω–Ω–æ–µ. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.",
        variant: "destructive",
      });
    }
  };

  const sizeClasses = {
    sm: "h-8 w-8",
    md: "h-10 w-10",
    lg: "h-12 w-12",
  };

  const iconSizes = {
    sm: 16,
    md: 20,
    lg: 24,
  };

  return (
    <Button
      variant={variant}
      size="icon"
      onClick={handleToggleFavorite}
      disabled={isLoading}
      className={cn(
        sizeClasses[size],
        "rounded-full transition-colors",
        isFavorited 
          ? "text-red-500 hover:text-red-600" 
          : "text-gray-400 hover:text-red-500",
        className
      )}
      data-testid={`button-favorite-${boxId}`}
    >
      <Heart
        size={iconSizes[size]}
        className={cn(
          "transition-all",
          isFavorited ? "fill-current" : "fill-none"
        )}
      />
    </Button>
  );
}

